import React, { Component } from "react";
import {useFormik} from "formik";

export default class EditProfile extends Component(){

  constructor(props) {
    super(props);


  const onSubmit = (values) => {
    alert(JSON.stringify(values));
  };


  const formik = useFormik({ initalValues: { fullName: "", address1: "", address2: "", state: "",
    city:"", zipcode:""}, validationOnBlur: true, onSubmit, })
}
render(){
    return (
      <form onSubmit = {formik.handleSubmit}>
        <div className="form-group">
          <input name="fullName"
          placeholder="Full Name"
          value={this.formik.values.fullName}
          onChange={formik.handleChange}
          />
        </div>
        <div className="form-group">
          <input name="address1"
          placeholder="Address 1"
          value={formik.values.fullName}
          onChange={formik.handleChange}
          />
        </div>
        <div className="form-group">
          <input name="address2"
          placeholder="Address 2"
          value={formik.values.fullName}
          onChange={formik.handleChange}
          />
        </div>
        <div className="form-group">
          <input name="state"
          placeholder="State"
          value={formik.values.fullName}
          onChange={formik.handleChange}
          />
        </div>
        <div className="form-group">
          <input name="city"
          placeholder="City"
          value={formik.values.fullName}
          onChange={formik.handleChange}
          />
        </div>
        <div className="form-group">
          <input name="zipcode"
          placeholder="Zipcode"
          value={formik.values.fullName}
          onChange={formik.handleChange}
          />
        </div>
        </form>

    )
}

}
